package com.nextlabs.bae.common;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStream;import java.util.Properties;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;public class PropertyLoader {	public static String propertiesPath;	public static String constantPath;	public static Properties bAESProperties;	public static Properties bAESConstant;	private static final Log LOG = LogFactory.getLog(PropertyLoader.class);	public static void loadProperties() {		bAESProperties = new Properties();		try {			LOG.info("PropertyLoader loadProperties(): Loading properties file at "					+ propertiesPath);			InputStream in = new FileInputStream(propertiesPath);			bAESProperties.load(in);			in.close();		} catch (IOException e) {			LOG.error("PropertyLoader loadProperties(): " + e.getMessage(), e);		}	}	public static void loadConstant() {		bAESConstant = new Properties();		try {			LOG.info("PropertyLoader loadConstant(): Loading constant file at "					+ constantPath);			InputStream in = new FileInputStream(constantPath);			bAESConstant.load(in);			in.close();		} catch (IOException e) {			LOG.error("PropertyLoader loadProperties(): " + e.getMessage(), e);		}	}}